mapper.select:
select * from t_house

    1=1

        and status = #{houseSearchVO.status}


        and address like concat('%',#{houseSearchVO.address},'%')


        and city like concat('%',#{houseSearchVO.city},'%')


        and rent_type = #{houseSearchVO.rentType}


        and month_rent >= #{houseSearchVO.minPrice}


        and month_rent <= #{houseSearchVO.maxPrice}


        and area >= #{houseSearchVO.minArea}


        and area <= #{houseSearchVO.maxArea}
